/**
 * 4. Modifica la página inicial realizada, incluyendo una imagen de cabecera en función de la estación del
 * año en la que nos encontremos y un color de fondo en función de la hora del día.
 * @autor: Hector H. Cevallos
 */

 $dia = date('173');
 
 if ( $dia < 79 ) {
     echo("<img src=\"data:image/jpeg;base64,\">");
 } elseif ( $dia < 172 ) {
     echo("<img src=\"data:image/jpeg;base64,\">");
 } elseif ( $dia < 265 ) {
     echo("<img src=\"data:image/jpeg;base64,\">");
 } elseif ( $dia < 352 ) {
     echo("<img src=\"data:image/jpeg;base64,\">");
 } else {
     echo("<img src=\"data:image/jpeg;base64,\">");
 }

 $hora = 2;

 if ($hora > 8 and $hora <= 10) {
     echo("<body style = \"background-color:rgb(245,176,65);\">");
 } else if ($hora > 12 and $hora < 20) {
    echo("<body style = \"background-color:rgb(93,173,226);\">");
} else if ($hora > 20 and $hora < 24) {
    echo("<body style = \"background-color:rgb(26,82,118);\">");
} else {
    echo("<body style = \"background-color:rgb(0,0,0);\">");
}
 echo("</body>");